SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET QUOTED_IDENTIFIER OFF
DECLARE @sql varchar(max) = "
declare @p_start_time datetime2;
declare @p_end_time datetime2;

select @p_start_time = st.local_time, @p_end_time = et.local_time 
from dbo.utc2local($__timeFrom()) as st join dbo.utc2local($__timeTo()) et on 1 = 1;

SELECT l2u.utc_time as time, (available_physical_memory_kb*1.0)/1024 as [Available Memory]
FROM (
		select collection_time, available_physical_memory_kb
		from [dbo].[dm_os_sys_memory]
		where collection_time BETWEEN @p_start_time AND @p_end_time
		--
		union all
		--
		select collection_time, available_physical_memory_kb
		from [dbo].[dm_os_sys_memory_aggregated]
		where collection_time BETWEEN @p_start_time AND @p_end_time
) AS data
cross apply dbo.local2utc(collection_time) as l2u
order by collection_time;
"
SET QUOTED_IDENTIFIER ON
IF ('$server' = SERVERPROPERTY('ServerName'))
BEGIN
  EXEC (@sql);
END;
ELSE
BEGIN
  EXEC (@sql) AT [$server];
END;

SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET QUOTED_IDENTIFIER OFF
DECLARE @sql varchar(max) = "
declare @p_start_time datetime2;
declare @p_end_time datetime2;

select @p_start_time = st.local_time, @p_end_time = et.local_time 
from dbo.utc2local($__timeFrom()) as st join dbo.utc2local($__timeTo()) et on 1 = 1;

SELECT  l2u.utc_time as time
		,metric = 'CPU - '+counter_name+' ('+[object_name]+')'
		,[value] = cntr_value/cpu_count
FROM (
		select  pc.collection_time ,pc.counter_name ,pc.[object_name] ,pc.cntr_value ,si.cpu_count
		from dbo.dm_os_performance_counters_nonsql as pc with (nolock)
		outer apply ( select top 1 cpu_count from dbo.dm_os_sys_info as si where si.collection_time <= pc.collection_time) as si
		where	(	collection_time BETWEEN @p_start_time AND @p_end_time )
			and	(	(pc.[object_name] = 'System' and counter_name = 'Processor Queue Length')
					--or
					--(pc.[object_name] = 'System' and counter_name = 'Context Switches/sec')
				)
		--
		union all
		--
		select  pc.collection_time ,pc.counter_name ,pc.[object_name] ,pc.cntr_value ,si.cpu_count
		from dbo.dm_os_performance_counters_nonsql_aggregated as pc with (nolock)
		outer apply ( select top 1 cpu_count from dbo.dm_os_sys_info as si where si.collection_time <= pc.collection_time) as si
		where	(	collection_time BETWEEN @p_start_time AND @p_end_time )
			and	(	(pc.[object_name] = 'System' and counter_name = 'Processor Queue Length')
					--or
					--(pc.[object_name] = 'System' and counter_name = 'Context Switches/sec')
				)
) AS data
cross apply dbo.local2utc(collection_time) as l2u
order by collection_time
"
SET QUOTED_IDENTIFIER ON
IF ('$server' = SERVERPROPERTY('ServerName'))
BEGIN
  EXEC (@sql);
END;
ELSE
BEGIN
  EXEC (@sql) AT [$server];
